
public class PrimeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
     int number=13 , i ;
     for(i=2;i<=number;i++)
     {
    	 
    	 if(number % i == 0)
    	     System.out.println(" it is not a prime number " );	
    	 else
    	     System.out.println("it is a prime number ");
     }
	 
	}
	
}
