
public class RainbowColour {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        String colour= "xyz";
        switch (colour) {
        
        case "violet":
  	     System.out.println("violet colour is present in rainbow");	 
	       break;
	    
        case "indigo":
     	     System.out.println("indigo colour is present in rainbow");	
     	    break;
     
        case "blue":
     	     System.out.println("blue colour is present in rainbow");	
     	    break;
   
        case "grey":
     	     System.out.println("grey colour is present in rainbow");	
     	    break;
    
        case "yellow":
     	     System.out.println("yellow colour is present in rainbow");	
     	    break;
   
        case "orange":
     	     System.out.println("orange colour is present in rainbow");	
     	    break;
  
        case "red":
     	     System.out.println("red colour is present in rainbow");
     	    break;
     	    
        default : System.out.println("this colour is not present in rainbow");

        }
	}
}
